def predict(self, X):
 X_transformed = X
 for step in self.steps[:-1]:
 # iterate over all but the final step
 # transform the data
 X_transformed = step[1].transform(X_transformed)
 # fit the last step
 return self.steps[-1][1].predict(X_transformed)

